query:
query SeekerResume($seekerResumeId: ID!) {
  seekerResume(id: $seekerResumeId) {
    ...resumeData
    __typename
  }
}

fragment resumeData on ProfResume {
  personal {
    ...resumePersonalInfo
    __typename
  }
  relocationCities {
    ...cvCity
    __typename
  }
  city {
    ...cvCity
    __typename
  }
  cityDistricts {
    ...cvCityDistricts
    __typename
  }
  contacts {
    ...cvContacts
    __typename
  }
  ...cvDesiredPosition
  ...experiences
  ...educations
  ...languages
  ...additionalEducations
  ...additionals
  ...cvState
  skills
  diiaCertificate
  resumeFilling {
    percentage
    __typename
  }
  __typename
}

fragment resumePersonalInfo on ResumePersonalInfo {
  surName
  photoUrl
  gender
  firstName
  fatherName
  birthDate
  age
  __typename
}

fragment cvCity on City {
  ua
  ru
  regionName
  name
  id
  __typename
}

fragment cvCityDistricts on CityDistrict {
  name
  id
  __typename
}

fragment cvContacts on ResumeContactInfo {
  email
  phone {
    isConfirmed
    value
    __typename
  }
  phones {
    value
    __typename
  }
  portfolios
  socials {
    ...cvSocialItem
    __typename
  }
  __typename
}

fragment cvSocialItem on ResumeSocialContactLink {
  value
  type
  __typename
}

fragment cvDesiredPosition on ProfResume {
  schedules {
    name
    id
    __typename
  }
  salary {
    currency
    amount
    __typename
  }
  title
  __typename
}

fragment experiences on ProfResume {
  experiences {
    ...experienceItem
    __typename
  }
  __typename
}

fragment experienceItem on ResumeExperience {
  startWork
  position
  id
  endWork
  description
  companyName
  branch {
    name
    id
    __typename
  }
  company {
    id
    branch {
      id
      __typename
    }
    __typename
  }
  __typename
}

fragment educations on ProfResume {
  educations {
    ...educationItem
    __typename
  }
  __typename
}

fragment educationItem on ResumeEducation {
  yearOfGraduation
  speciality
  location
  level
  institutionTitle
  id
  importSource
  __typename
}

fragment languages on ProfResume {
  languageSkills {
    ...languageSkillItem
    __typename
  }
  __typename
}

fragment languageSkillItem on ResumeLanguageSkill {
  certificate
  isCanPassInterview
  language {
    id
    name
    __typename
  }
  level {
    name
    id
    __typename
  }
  __typename
}

fragment additionalEducations on ProfResume {
  additionalEducations {
    ...courseItem
    __typename
  }
  __typename
}

fragment courseItem on ResumeAdditionalEducation {
  yearOfGraduation
  title
  location
  id
  description
  __typename
}

fragment additionals on ProfResume {
  additionals {
    ...additionalItem
    __typename
  }
  __typename
}

fragment additionalItem on ResumeAdditionalInfo {
  id
  name
  description
  __typename
}

fragment cvState on ProfResume {
  updateDate
  state {
    state
    availabilityState
    banInfo {
      banReason {
        id
        text3
        text3Ukr
        text2
        text2Ukr
        __typename
      }
      __typename
    }
    hiddenCompanies {
      ...cvCompany
      __typename
    }
    searchState
    isAnonymous
    isAllowedToShareWithPartners
    privacySettings {
      hasHiddenPhones
      __typename
    }
    __typename
  }
  __typename
}

fragment cvCompany on Company {
  id
  name
  __typename
}
